import { useState, useEffect } from 'react';
import { ChevronLeft, ChevronRight, Bell, Search, User, Calendar, List, Settings, Plus, X, Clock, MapPin, Video, Repeat, Tag, AlertCircle } from 'lucide-react';

// Sample event data (simulating JSON load)
const sampleEvents = [
  {
    id: 1,
    title: "Team Meeting",
    date: new Date().toISOString().split('T')[0],
    startTime: "10:00",
    endTime: "11:00",
    category: "Work",
    tags: ["Meeting"],
    location: "Conference Room A",
    hasVideoLink: true
  },
  {
    id: 2,
    title: "Project Review",
    date: new Date().toISOString().split('T')[0],
    startTime: "14:00",
    endTime: "15:30",
    category: "Work",
    tags: ["Meeting", "Deadline"],
    hasVideoLink: false
  },
  {
    id: 3,
    title: "Doctor Appointment",
    date: new Date().toISOString().split('T')[0],
    startTime: "16:00",
    endTime: "17:00",
    category: "Personal",
    tags: [],
    location: "Medical Center"
  }
];

// Sample tasks data
const sampleTasks = [
  {
    id: 1,
    title: "Buy groceries",
    completed: false,
    category: "Personal",
    dueDate: new Date().toISOString().split('T')[0]
  },
  {
    id: 2,
    title: "Finish project report",
    completed: false,
    category: "Work",
    dueDate: new Date().toISOString().split('T')[0]
  },
  {
    id: 3,
    title: "Call mom",
    completed: true,
    category: "Personal",
    dueDate: new Date().toISOString().split('T')[0]
  }
];

// List of all available tags
const availableTags = ["Meeting", "Deadline", "Call", "Urgent", "Follow-up"];
const categories = ["Work", "Personal", "Urgent"];

// Calendar Utilities
const daysOfWeek = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

const generateMonthDays = (year, month) => {
  const result = [];
  const firstDay = new Date(year, month, 1).getDay();
  const daysInMonth = new Date(year, month + 1, 0).getDate();

  // Add empty slots for days before the first of the month
  for (let i = 0; i < firstDay; i++) {
    result.push(null);
  }

  // Add days of the month
  for (let i = 1; i <= daysInMonth; i++) {
    result.push(i);
  }

  return result;
};

const getCurrentTimeOffset = () => {
  const now = new Date();
  const startOfDay = new Date(now);
  startOfDay.setHours(7, 0, 0, 0);
  const diffInMinutes = (now - startOfDay) / (1000 * 60);
  return diffInMinutes;
};

function App() {
  const now = new Date();
  const [selectedDate, setSelectedDate] = useState(now);
  const [currentView, setCurrentView] = useState('Day');
  const [events, setEvents] = useState(sampleEvents);
  const [tasks, setTasks] = useState(sampleTasks);
  const [showModal, setShowModal] = useState(false);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedTags, setSelectedTags] = useState([]);
  const [timeIndicatorOffset, setTimeIndicatorOffset] = useState(getCurrentTimeOffset());
  const [searchQuery, setSearchQuery] = useState('');
  const [newEvent, setNewEvent] = useState({
    title: '',
    date: now.toISOString().split('T')[0],
    startTime: '09:00',
    endTime: '10:00',
    category: 'Work',
    tags: [],
    location: '',
    hasVideoLink: false,
    isTask: false
  });

  // Update time indicator
  useEffect(() => {
    const interval = setInterval(() => {
      setTimeIndicatorOffset(getCurrentTimeOffset());
    }, 60000);
    return () => clearInterval(interval);
  }, []);

  // Navigation handlers
  const goToNextMonth = () => {
    const nextMonth = new Date(selectedDate);
    nextMonth.setMonth(nextMonth.getMonth() + 1);
    setSelectedDate(nextMonth);
  };

  const goToPreviousMonth = () => {
    const prevMonth = new Date(selectedDate);
    prevMonth.setMonth(prevMonth.getMonth() - 1);
    setSelectedDate(prevMonth);
  };

  const goToNextDay = () => {
    const nextDay = new Date(selectedDate);
    nextDay.setDate(nextDay.getDate() + 1);
    setSelectedDate(nextDay);
  };

  const goToPreviousDay = () => {
    const prevDay = new Date(selectedDate);
    prevDay.setDate(prevDay.getDate() - 1);
    setSelectedDate(prevDay);
  };

  const selectDay = (day) => {
    if (day) {
      const newDate = new Date(selectedDate);
      newDate.setDate(day);
      setSelectedDate(newDate);
    }
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const toggleCategory = (category) => {
    setSelectedCategories(prev => 
      prev.includes(category) 
        ? prev.filter(c => c !== category) 
        : [...prev, category]
    );
  };

  const toggleTag = (tag) => {
    setSelectedTags(prev => 
      prev.includes(tag) 
        ? prev.filter(t => t !== tag) 
        : [...prev, tag]
    );
  };

  const clearAllFilters = () => {
    setSelectedCategories([]);
    setSelectedTags([]);
    setSearchQuery('');
  };

  const toggleTaskCompleted = (taskId) => {
    setTasks(prev => 
      prev.map(task => 
        task.id === taskId ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const getFormattedDate = (date) => {
    return `${date.getDate()} ${months[date.getMonth()]}, ${daysOfWeek[date.getDay()]}`;
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setNewEvent(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const toggleEventType = (isTask) => {
    setNewEvent(prev => ({
      ...prev,
      isTask
    }));
  };

  const handleTagSelection = (tag) => {
    setNewEvent(prev => {
      if (prev.tags.includes(tag)) {
        return {
          ...prev,
          tags: prev.tags.filter(t => t !== tag)
        };
      } else {
        return {
          ...prev,
          tags: [...prev.tags, tag]
        };
      }
    });
  };

  const saveNewItem = () => {
    if (!newEvent.title.trim()) {
      alert('Please enter a title');
      return;
    }

    if (newEvent.isTask) {
      // Save as task
      const newTask = {
        id: tasks.length > 0 ? Math.max(...tasks.map(t => t.id)) + 1 : 1,
        title: newEvent.title,
        completed: false,
        category: newEvent.category,
        dueDate: newEvent.date
      };
      setTasks([...tasks, newTask]);
    } else {
      // Save as event
      const newEventObj = {
        id: events.length > 0 ? Math.max(...events.map(e => e.id)) + 1 : 1,
        title: newEvent.title,
        date: newEvent.date,
        startTime: newEvent.startTime,
        endTime: newEvent.endTime,
        category: newEvent.category,
        tags: newEvent.tags,
        location: newEvent.location,
        hasVideoLink: newEvent.hasVideoLink
      };
      setEvents([...events, newEventObj]);
    }

    // Reset form and close modal
    setNewEvent({
      title: '',
      date: now.toISOString().split('T')[0],
      startTime: '09:00',
      endTime: '10:00',
      category: 'Work',
      tags: [],
      location: '',
      hasVideoLink: false,
      isTask: false
    });
    setShowModal(false);
  };

  const filteredTasks = tasks.filter(task => {
    // Filter by category
    const matchesCategory = selectedCategories.length === 0 || selectedCategories.includes(task.category);

    // Filter by search query
    const matchesSearch = !searchQuery || 
      task.title.toLowerCase().includes(searchQuery.toLowerCase());

    return matchesCategory && matchesSearch;
  });

  const filteredEvents = events.filter(event => {
    // Filter by category
    const matchesCategory = selectedCategories.length === 0 || selectedCategories.includes(event.category);

    // Filter by tags
    const matchesTags = selectedTags.length === 0 || 
      (event.tags && event.tags.some(tag => selectedTags.includes(tag)));

    // Filter by search query
    const matchesSearch = !searchQuery || 
      event.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (event.location && event.location.toLowerCase().includes(searchQuery.toLowerCase()));

    return matchesCategory && matchesTags && matchesSearch;
  });

  return (
    <div className="flex flex-col h-screen bg-gray-50">
      {/* Top Bar */}
      <header className="bg-white border-b border-gray-200 px-4 py-2 flex items-center justify-between shadow-sm">
        <div className="flex items-center">
          <h1 className="text-xl font-bold text-blue-600">My Planner</h1>
        </div>

        <div className="flex items-center space-x-2 md:space-x-6 flex-1 justify-center">
          <div className="relative w-64">
            <input 
              type="text" 
              placeholder="Search events & tasks..." 
              className="pl-8 pr-2 py-1 border border-gray-300 rounded-lg w-full focus:outline-none focus:ring-1 focus:ring-blue-500"
              value={searchQuery}
              onChange={handleSearchChange}
            />
            <Search className="absolute left-2 top-2 h-4 w-4 text-gray-400" />
          </div>

          <nav className="hidden md:flex space-x-4">
            <button className="flex items-center text-gray-600 hover:text-blue-600" onClick={() => setCurrentView('Month')}>
              <Calendar className="h-4 w-4 mr-1" />
              <span>Calendar</span>
            </button>
            <button className="flex items-center text-gray-600 hover:text-blue-600" onClick={() => setCurrentView('Agenda')}>
              <List className="h-4 w-4 mr-1" />
              <span>Agenda</span>
            </button>
            <button className="flex items-center text-gray-600">
              <Settings className="h-4 w-4 mr-1" />
              <span>Settings</span>
            </button>
          </nav>
        </div>

        <div className="flex items-center space-x-4">
          <button className="relative">
            <Bell className="h-5 w-5 text-gray-600" />
            <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full w-4 h-4 flex items-center justify-center">2</span>
          </button>
          <button className="flex items-center space-x-2">
            <div className="h-8 w-8 bg-blue-500 rounded-full flex items-center justify-center">
              <User className="h-5 w-5 text-white" />
            </div>
          </button>
        </div>
      </header>

      {/* Main Toolbar */}
      <div className="bg-white px-4 py-3 border-b border-gray-200 flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <h2 className="text-lg font-medium">{getFormattedDate(selectedDate)}</h2>
          <button 
            className="px-3 py-1 bg-blue-100 text-blue-600 rounded-md hover:bg-blue-200"
            onClick={() => setSelectedDate(new Date())}
          >
            Today
          </button>
        </div>
        <div className="flex items-center space-x-2">
          <button className="p-1 rounded hover:bg-gray-100" onClick={currentView === 'Month' ? goToPreviousMonth : goToPreviousDay}>
            <ChevronLeft className="h-5 w-5 text-gray-600" />
          </button>
          <button className="p-1 rounded hover:bg-gray-100" onClick={currentView === 'Month' ? goToNextMonth : goToNextDay}>
            <ChevronRight className="h-5 w-5 text-gray-600" />
          </button>
          <div className="border-l border-gray-300 h-6 mx-2"></div>
          <div className="flex bg-gray-100 rounded-lg p-1">
            {['Day', 'Week', 'Month', 'Agenda'].map(view => (
              <button 
                key={view}
                className={`px-3 py-1 text-sm rounded-md ${currentView === view ? 'bg-white shadow-sm text-blue-600' : 'text-gray-600'}`}
                onClick={() => setCurrentView(view)}
              >
                {view}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Main Content Area */}
      <div className="flex flex-1 overflow-hidden">
        {/* Sidebar */}
        <aside className="w-64 bg-white border-r border-gray-200 flex flex-col">
          {/* Mini Calendar */}
          <div className="p-4 border-b border-gray-200">
            <div className="flex justify-between items-center mb-2">
              <h3 className="font-medium">{months[selectedDate.getMonth()]} {selectedDate.getFullYear()}</h3>
              <div className="flex space-x-1">
                <button className="p-1 rounded hover:bg-gray-100" onClick={goToPreviousMonth}>
                  <ChevronLeft className="h-3 w-3 text-gray-600" />
                </button>
                <button className="p-1 rounded hover:bg-gray-100" onClick={goToNextMonth}>
                  <ChevronRight className="h-3 w-3 text-gray-600" />
                </button>
              </div>
            </div>
            <div className="grid grid-cols-7 gap-1 text-center text-xs mb-1">
              {daysOfWeek.map(day => (
                <div key={day} className="text-gray-500">{day[0]}</div>
              ))}
            </div>
            <div className="grid grid-cols-7 gap-1 text-center">
              {generateMonthDays(selectedDate.getFullYear(), selectedDate.getMonth()).map((day, i) => (
                <button 
                  key={i}
                  className={`h-6 w-6 flex items-center justify-center text-xs rounded-full
                    ${!day ? 'invisible' : ''}
                    ${day === selectedDate.getDate() ? 'bg-blue-500 text-white' : 'hover:bg-gray-100'}`}
                  disabled={!day}
                  onClick={() => selectDay(day)}
                >
                  {day}
                </button>
              ))}
            </div>
          </div>

          {/* To-Do list & Filters */}
          <div className="p-4 border-b border-gray-200 flex-1 overflow-y-auto">
            <div className="flex justify-between items-center mb-3">
              <h3 className="font-medium">Tasks</h3>
              <button className="text-xs text-blue-600 hover:underline" onClick={clearAllFilters}>
                Clear all filters
              </button>
            </div>

            {/* Category filters */}
            <div className="mb-4">
              <h4 className="text-xs text-gray-500 mb-1">Categories</h4>
              <div className="flex flex-wrap gap-2">
                {categories.map(category => (
                  <button
                    key={category}
                    className={`px-2 py-1 rounded-full text-xs 
                      ${selectedCategories.includes(category) 
                        ? 'bg-blue-500 text-white' 
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
                    onClick={() => toggleCategory(category)}
                  >
                    {category}
                  </button>
                ))}
              </div>
            </div>

            {/* Tags */}
            <div className="mb-4">
              <h4 className="text-xs text-gray-500 mb-1">Tags</h4>
              <div className="flex flex-wrap gap-2">
                {availableTags.map(tag => (
                  <button
                    key={tag}
                    className={`px-2 py-1 rounded-full text-xs
                      ${selectedTags.includes(tag) 
                        ? 'bg-blue-500 text-white' 
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
                    onClick={() => toggleTag(tag)}
                  >
                    {tag}
                  </button>
                ))}
              </div>
            </div>

            {/* Task list */}
            <div>
              <h4 className="text-xs text-gray-500 mb-2">Todo List</h4>
              {filteredTasks.length === 0 ? (
                <p className="text-sm text-gray-500 italic">No tasks found</p>
              ) : (
                <ul className="space-y-2">
                  {filteredTasks.map(task => (
                    <li key={task.id} className="flex items-center">
                      <input 
                        type="checkbox" 
                        checked={task.completed} 
                        onChange={() => toggleTaskCompleted(task.id)}
                        className="mr-2 h-4 w-4 rounded border-gray-300 focus:ring-blue-500"
                      />
                      <span className={`text-sm ${task.completed ? 'line-through text-gray-400' : 'text-gray-700'}`}>
                        {task.title}
                      </span>
                      <span className="ml-auto text-xs text-gray-500">
                        {task.category}
                      </span>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        </aside>

        {/* Main Schedule Grid */}
        <div className="flex-1 overflow-y-auto relative">
          {currentView === 'Day' && (
            <div className="flex h-full">
              {/* Time column */}
              <div className="w-16 bg-white border-r border-gray-200 pt-4">
                {Array.from({ length: 14 }).map((_, i) => (
                  <div key={i} className="h-60 border-b border-gray-100 text-xs text-gray-500 px-2">
                    {i + 7}:00
                  </div>
                ))}
              </div>

              {/* Event grid */}
              <div className="flex-1 relative">
                {/* Time indicator */}
                <div 
                  className="absolute left-0 right-0 border-t-2 border-red-500 z-10 flex items-center"
                  style={{ top: `${(timeIndicatorOffset / 60) * 60}px` }}
                >
                  <div className="w-2 h-2 rounded-full bg-red-500 -ml-1 -mt-1"></div>
                </div>

                {/* Time grid */}
                {Array.from({ length: 14 }).map((_, i) => (
                  <div key={i} className="h-60 border-b border-gray-100"></div>
                ))}

                {/* Events */}
                {filteredEvents
                  .filter(event => {
                    const eventDate = new Date(event.date);
                    return eventDate.getDate() === selectedDate.getDate() && 
                           eventDate.getMonth() === selectedDate.getMonth() &&
                           eventDate.getFullYear() === selectedDate.getFullYear();
                  })
                  .map(event => {
                    // Calculate event position and height
                    const startHour = parseInt(event.startTime.split(':')[0]);
                    const startMinute = parseInt(event.startTime.split(':')[1]);
                    const endHour = parseInt(event.endTime.split(':')[0]);
                    const endMinute = parseInt(event.endTime.split(':')[1]);

                    const startOffset = ((startHour - 7) * 60 + startMinute) * 1;
                    const duration = ((endHour - startHour) * 60 + (endMinute - startMinute)) * 1;

                    const bgColors = {
                      'Work': 'bg-blue-100 border-blue-300',
                      'Personal': 'bg-green-100 border-green-300',
                      'Urgent': 'bg-red-100 border-red-300'
                    };

                    const bgColor = bgColors[event.category] || 'bg-gray-100 border-gray-300';

                    return (
                      <div
                        key={event.id}
                        className={`absolute left-4 right-4 p-2 rounded-md border ${bgColor} shadow-sm`}
                        style={{ 
                          top: `${startOffset}px`,
                          height: `${duration}px`
                        }}
                      >
                        <div className="font-medium text-gray-800">{event.title}</div>
                        <div className="flex items-center text-xs text-gray-600 mt-1">
                          <Clock className="h-3 w-3 mr-1" />
                          <span>{event.startTime} - {event.endTime}</span>

                          {event.location && (
                            <div className="flex items-center ml-2">
                              <MapPin className="h-3 w-3 mr-1" />
                              <span>{event.location}</span>
                            </div>
                          )}

                          {event.hasVideoLink && (
                            <div className="flex items-center ml-2">
                              <Video className="h-3 w-3" />
                            </div>
                          )}
                        </div>

                        {event.tags && event.tags.length > 0 && (
                          <div className="flex flex-wrap gap-1 mt-1">
                            {event.tags.map(tag => (
                              <span key={tag} className="bg-white px-1 rounded text-xs border border-gray-200">
                                {tag}
                              </span>
                            ))}
                          </div>
                        )}
                      </div>
                    );
                })}
              </div>
            </div>
          )}

          {currentView === 'Week' && (
            <div className="p-4 text-center">
              <h3 className="text-xl text-gray-500">Week View</h3>
              <p className="text-sm text-gray-400 mt-2">Weekly calendar view would be displayed here</p>
            </div>
          )}

          {currentView === 'Month' && (
            <div className="p-4 text-center">
              <h3 className="text-xl text-gray-500">Month View</h3>
              <p className="text-sm text-gray-400 mt-2">Monthly calendar view would be displayed here</p>
            </div>
          )}

          {currentView === 'Agenda' && (
            <div className="p-6">
              <h3 className="text-lg font-medium mb-4">Agenda View</h3>

              {filteredEvents.length === 0 ? (
                <p className="text-gray-500 italic">No events found</p>
              ) : (
                <div className="space-y-4">
                  {filteredEvents.map(event => {
                    const bgColors = {
                      'Work': 'bg-blue-50 border-blue-200',
                      'Personal': 'bg-green-50 border-green-200',
                      'Urgent': 'bg-red-50 border-red-200'
                    };

                    const bgColor = bgColors[event.category] || 'bg-gray-50 border-gray-200';

                    return (
                      <div 
                        key={event.id} 
                        className={`p-3 rounded-md border ${bgColor}`}
                      >
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="font-medium">{event.title}</h4>
                            <div className="text-sm text-gray-600 mt-1">
                              {new Date(event.date).toLocaleDateString('en-US', {
                                weekday: 'short',
                                month: 'short',
                                day: 'numeric'
                              })}
                              , {event.startTime} - {event.endTime}
                            </div>
                          </div>
                          <span className="text-xs bg-gray-100 px-2 py-1 rounded-full">
                            {event.category}
                          </span>
                        </div>

                        <div className="flex flex-wrap mt-2 gap-2">
                          {event.location && (
                            <div className="flex items-center text-xs text-gray-600">
                              <MapPin className="h-3 w-3 mr-1" />
                              <span>{event.location}</span>
                            </div>
                          )}

                          {event.hasVideoLink && (
                            <div className="flex items-center text-xs text-gray-600">
                              <Video className="h-3 w-3 mr-1" />
                              <span>Video conference</span>
                            </div>
                          )}
                        </div>

                        {event.tags && event.tags.length > 0 && (
                          <div className="flex flex-wrap gap-1 mt-2">
                            {event.tags.map(tag => (
                              <span key={tag} className="bg-white px-1 rounded text-xs border border-gray-200">
                                {tag}
                              </span>
                            ))}
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Floating Add Button */}
      <button 
        className="absolute bottom-6 right-6 bg-blue-500 hover:bg-blue-600 text-white rounded-full p-3 shadow-lg"
        onClick={() => setShowModal(true)}
      >
        <Plus className="h-6 w-6" />
      </button>

      {/* Create Item Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl p-6 w-96">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-medium">Create New Item</h3>
              <button onClick={() => setShowModal(false)}>
                <X className="h-5 w-5 text-gray-500 hover:text-gray-700" />
              </button>
            </div>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Title</label>
                <input 
                  type="text" 
                  name="title"
                  value={newEvent.title}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  placeholder="Enter title" 
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Type</label>
                <div className="flex space-x-2">
                  <button 
                    className={`flex-1 py-2 rounded-md ${!newEvent.isTask ? 'bg-blue-500 text-white' : 'bg-gray-100 text-gray-700'}`}
                    onClick={() => toggleEventType(false)}
                  >
                    Event
                  </button>
                  <button 
                    className={`flex-1 py-2 rounded-md ${newEvent.isTask ? 'bg-blue-500 text-white' : 'bg-gray-100 text-gray-700'}`}
                    onClick={() => toggleEventType(true)}
                  >
                    Task
                  </button>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-2">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Date</label>
                  <input 
                    type="date" 
                    name="date"
                    value={newEvent.date}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Category</label>
                  <select 
                    className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    name="category"
                    value={newEvent.category}
                    onChange={handleInputChange}
                  >
                    {categories.map(category => (
                      <option key={category} value={category}>{category}</option>
                    ))}
                  </select>
                </div>
              </div>

              {!newEvent.isTask && (
                <>
                  <div className="grid grid-cols-2 gap-2">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Start Time</label>
                      <input 
                        type="time" 
                        name="startTime"
                        value={newEvent.startTime}
                        onChange={handleInputChange}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">End Time</label>
                      <input 
                        type="time" 
                        name="endTime"
                        value={newEvent.endTime}
                        onChange={handleInputChange}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md"
                      />
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Location</label>
                    <input 
                      type="text" 
                      name="location"
                      value={newEvent.location}
                      onChange={handleInputChange}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                      placeholder="Enter location (optional)" 
                    />
                  </div>

                  <div className="flex items-center">
                    <input 
                      type="checkbox" 
                      id="hasVideoLink"
                      name="hasVideoLink"
                      checked={newEvent.hasVideoLink}
                      onChange={handleInputChange}
                      className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                    />
                    <label htmlFor="hasVideoLink" className="ml-2 block text-sm text-gray-700">
                      Has video conference
                    </label>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Tags</label>
                    <div className="flex flex-wrap gap-2">
                      {availableTags.map(tag => (
                        <button
                          key={tag}
                          type="button"
                          className={`px-2 py-1 rounded-full text-xs 
                            ${newEvent.tags.includes(tag) 
                              ? 'bg-blue-500 text-white' 
                              : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
                          onClick={() => handleTagSelection(tag)}
                        >
                          {tag}
                        </button>
                      ))}
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Repeat</label>
                    <div className="flex items-center space-x-1">
                      <Repeat className="h-4 w-4 text-gray-500" />
                      <select className="flex-1 px-3 py-2 border border-gray-300 rounded-md">
                        <option>None</option>
                        <option>Daily</option>
                        <option>Weekly</option>
                        <option>Monthly</option>
                        <option>Custom</option>
                      </select>
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Reminder</label>
                    <div className="flex items-center space-x-1">
                      <AlertCircle className="h-4 w-4 text-gray-500" />
                      <select className="flex-1 px-3 py-2 border border-gray-300 rounded-md">
                        <option>None</option>
                        <option>10 minutes before</option>
                        <option>30 minutes before</option>
                        <option>1 hour before</option>
                        <option>1 day before</option>
                      </select>
                    </div>
                  </div>
                </>
              )}

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Notes</label>
                <textarea 
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  rows="3"
                  placeholder="Add notes or description"
                  name="notes"
                  onChange={handleInputChange}
                />
              </div>

              <div className="flex justify-end space-x-2 pt-2">
                <button 
                  className="px-4 py-2 text-gray-700 hover:bg-gray-100 rounded-md"
                  onClick={() => setShowModal(false)}
                >
                  Cancel
                </button>
                <button 
                  className="px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded-md"
                  onClick={saveNewItem}
                >
                  Save
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
