import React from 'react';
import dayjs from 'dayjs';

const Header = ({ currentMonth, onNextMonth, onPrevMonth }) => {
  return (
    <div className="flex items-center justify-between p-6 bg-gradient-to-r from-blue-500 to-blue-700 text-white">
      <button 
        onClick={onPrevMonth} 
        className="px-4 py-2 bg-white text-blue-700 rounded-lg hover:bg-blue-50 transition-colors flex items-center font-semibold shadow-md"
      >
        <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
        </svg>
        Previous
      </button>
      <h2 className="text-2xl font-bold drop-shadow-sm">
        {dayjs(currentMonth).format('MMMM YYYY')}
      </h2>
      <button 
        onClick={onNextMonth} 
        className="px-4 py-2 bg-white text-blue-700 rounded-lg hover:bg-blue-50 transition-colors flex items-center font-semibold shadow-md"
      >
        Next
        <svg className="w-4 h-4 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
        </svg>
      </button>
    </div>
  );
};

export default Header;
