import React, { useState, useEffect } from 'react';
import dayjs from 'dayjs';
import Header from './components/Header';
import Calendar from './components/Calendar';
import './App.css';

function App() {
  const [currentMonth, setCurrentMonth] = useState(dayjs());
  const [events, setEvents] = useState([]);

  useEffect(() => {
    // Fetch events from the static JSON file
    fetch('/events.json')
      .then(response => response.json())
      .then(data => setEvents(data))
      .catch(error => console.error('Error fetching events:', error));
  }, []);

  const handleNextMonth = () => {
    setCurrentMonth(currentMonth.add(1, 'month'));
  };

  const handlePrevMonth = () => {
    setCurrentMonth(currentMonth.subtract(1, 'month'));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-purple-100 py-8">
      <div className="container mx-auto px-4 max-w-5xl">
        <h1 className="text-3xl font-bold text-center mb-8 text-blue-800 drop-shadow-sm">
          Calendar Application
        </h1>
        <div className="bg-white rounded-xl shadow-xl overflow-hidden border border-gray-200">
          <Header 
            currentMonth={currentMonth}
            onNextMonth={handleNextMonth}
            onPrevMonth={handlePrevMonth}
          />
          <Calendar currentMonth={currentMonth} events={events} />
        </div>
        <div className="mt-6 text-center text-gray-600 text-sm">
          Â© 2024 Calendar App - All rights reserved
        </div>
      </div>
    </div>
  );
}

export default App;
